        static public HostsL DeSerial(string path)
        {
            XmlSerializer xmlSerializer = new XmlSerializer(typeof(HostsL));
            using (FileStream fs = new FileStream(path, FileMode.Open))
            {
                return (HostsL)xmlSerializer.Deserialize(fs);
            }
        }

        static public void Wrong()
        {
            Console.Clear();
            Console.ForegroundColor = ConsoleColor.Red;
            Console.WriteLine("Неправильный ввод!");
            Console.ForegroundColor = ConsoleColor.White;
            ViewInfo();
        }
        static public void CompareFiles(string frst, string sec)
        {
            HostsL hosts1 = DeSerial(frst);
            HostsL hosts2 = DeSerial(sec);
            Console.WriteLine(frst);

            foreach (var item in hosts2.Hosts_list)
            {
                foreach (var item2 in hosts1.Hosts_list)
                {
                    if (item.HostName == item2.HostName)
                    {
                        foreach (var PortItem1 in item2.Ports_list)
                        {
                            if (item.Ports_list.Where(c => c.PortNumber == PortItem1.PortNumber).Count() == 0)
                            {
                                Console.WriteLine(PortItem1.PortNumber + " in " + item2.HostName + " -");
                            }
                        }
                    }
                }
            }
            Console.WriteLine();
            Console.WriteLine(sec);
            foreach (var item in hosts1.Hosts_list)
            {
                foreach (var item2 in hosts2.Hosts_list)
                {
                    if (item.HostName == item2.HostName)
                    {
                        foreach (var PortItem1 in item2.Ports_list)
                        {
                            if (item.Ports_list.Where(c => c.PortNumber == PortItem1.PortNumber).Count() == 0)
                            {
                                Console.WriteLine(PortItem1.PortNumber + " in " + item2.HostName + " +");
                            }
                        }
                    }
                }
            }
            Console.ReadLine();
        }
        static public void PrintInfo(HostsL hostsL)
        {
            foreach (Host host in hostsL.Hosts_list)
            {
                Console.WriteLine(" " + host.HostName);
                foreach (Port port in host.Ports_list)
                {
                    Console.ForegroundColor = ConsoleColor.Yellow;
                    Console.WriteLine("   " + port.PortNumber);
                }
                Console.ForegroundColor = ConsoleColor.White;
            }
        }
        static public void ViewInfo()
        {
            string[] str = Directory.GetFiles("_info/");

            foreach (string str2 in str)
            {
                HostsL hosts = DeSerial(str2);
                Console.ForegroundColor = ConsoleColor.Green;
                Console.WriteLine(str2);
                Console.ForegroundColor = ConsoleColor.White;
                PrintInfo(hosts);
            }
            Console.WriteLine("\n\nДля сравнения выберите два файла под их номерами");
            int i = 0;
            foreach (string str2 in str)
            {
                Console.WriteLine(i + " " + str2);
                i++;
            }
            string First = Console.ReadLine();
            string Second = Console.ReadLine();
            //TODO EX
            if (First == Second)
            {
                Wrong();
            }
            if (Int32.Parse(First) > Int32.Parse(Second))
            {
                Wrong();
            }
            Console.Clear();
            try
            {
                First = str[Int32.Parse(First)];
                Second = str[Int32.Parse(Second)];
                CompareFiles(First, Second);
            }
            catch
            {
                Wrong();
            }
            Console.ReadLine();
            Console.Clear();
            ViewInfo();
        }

